returnStdout: true
).trim()
retry = sh (
script: &quot;sed &apos;s/[\t ][\t ]*/ /g&apos; &lt; out | sed &apos;2q;d&apos; | cut -d&apos; &apos; -f4&quot;,
returnStdout: true
).trim().toInteger();
}
if(status != &quot;Running&quot;){
try {
sh &quot;&quot;&quot;oc logs \$(oc get pods -l app=${props.name} --sort-by=.metadata.creationTimestamp -o name | tail -n 1)&quot;&quot;&quot;
