private static final By[] selectorsImages = { By.cssSelector(&quot;div &gt; img:nth-child(2)&quot;),
By.cssSelector(&quot;div &gt; img:nth-child(3)&quot;),
By.cssSelector(&quot;div &gt; img:nth-child(4)&quot;) };
